import { Icon } from "@iconify/react";

const activejobs = [
  { name: "Remote", icon: "heroicons:home" },
  { name: "MNC", icon: "heroicons:building-office" },
  { name: "HR", icon: "heroicons:user-group" },
  { name: "Analytics", icon: "heroicons:magnifying-glass-circle" },
  { name: "Project Mgmt", icon: "heroicons:check-circle" },
  { name: "Marketing", icon: "heroicons:chart-bar" },
  { name: "Sales", icon: "heroicons:gift" },
  { name: "Fresher", icon: "heroicons:academic-cap" },
  { name: "Engineering", icon: "heroicons:cog-6-tooth" },
  { name: "Internship", icon: "heroicons:document-text" },
  { name: "Data Science", icon: "heroicons:chart-pie" },
];

const CategoryCard = ({ name, icon }) => (
  <div className="flex items-center justify-between p-4 rounded-xl border bg-white shadow-sm hover:shadow-md cursor-pointer w-44">
    <div className="flex items-center gap-3">
      <span className="p-2 rounded-full bg-gray-100">
        <Icon icon={icon} className="text-xl text-gray-700" />
      </span>
      <span className="font-medium text-gray-800">{name}</span>
    </div>
    <Icon icon="heroicons:chevron-right" className="text-gray-500" />
  </div>
);

const CategoriesGrid = () => {
  return (
    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
      {categories.map((cat, index) => (
        <CategoryCard key={index} name={cat.name} icon={cat.icon} />
      ))}
    </div>
  );
};

export default ActiveJobs;
