import React, { useState } from "react";

// Function to generate random candidate scores
const generateCandidateScores = (numCandidates) => {
  const candidates = [];
  for (let i = 0; i < numCandidates; i++) {
    const aptitude = Math.floor(Math.random() * 51) + 50; // 50–100
    const coding = Math.floor(Math.random() * 51) + 50;
    const communication = Math.floor(Math.random() * 51) + 50;
    const total = aptitude + coding + communication;

    candidates.push({
      Candidate: `Candidate_${i + 1}`,
      Aptitude: aptitude,
      Coding: coding,
      Communication: communication,
      Total_Score: total,
    });
  }
  return candidates;
};

export default function CandidateGenerator() {
  const [numCandidates, setNumCandidates] = useState(50);
  const [numSelections, setNumSelections] = useState(10);
  const [candidates, setCandidates] = useState([]);
  const [showAll, setShowAll] = useState(false);

  // Generate random candidates
  const handleGenerate = () => {
    const generated = generateCandidateScores(numCandidates);
    setCandidates(generated);
  };

  // Sort and take top candidates
  const topCandidates = [...candidates]
    .sort((a, b) => b.Total_Score - a.Total_Score)
    .slice(0, numSelections);

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100 p-6">
      <div className="w-full max-w-5xl bg-white shadow-lg rounded-xl p-6">
        <h1 className="text-2xl font-bold mb-4">Top Candidate Recommendation</h1>

        {/* Controls */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <div>
            <label className="block mb-2 font-medium">
              Enter total number of candidates shortlisted:
            </label>
            <input
              type="number"
              value={numCandidates}
              min={10}
              max={1000}
              onChange={(e) => setNumCandidates(Number(e.target.value))}
              className="w-full border px-3 py-2 rounded-lg shadow-sm"
            />
          </div>
          <div>
            <label className="block mb-2 font-medium">
              Number of top candidates to select:
            </label>
            <input
              type="number"
              value={numSelections}
              min={1}
              max={Math.min(numCandidates, 50)}
              onChange={(e) => setNumSelections(Number(e.target.value))}
              className="w-full border px-3 py-2 rounded-lg shadow-sm"
            />
          </div>
        </div>

        <button
          onClick={handleGenerate}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
        >
          Generate Recommendations
        </button>

        {/* Results */}
        {candidates.length > 0 && (
          <div className="mt-6">
            <h2 className="text-xl font-semibold mb-3">
              Top {numSelections} Candidates Based on Assessment Scores
            </h2>
            <table className="w-full border-collapse border border-gray-300">
              <thead>
                <tr className="bg-gray-200">
                  <th className="border p-2">#</th>
                  <th className="border p-2">Candidate</th>
                  <th className="border p-2">Aptitude</th>
                  <th className="border p-2">Coding</th>
                  <th className="border p-2">Communication</th>
                  <th className="border p-2">Total Score</th>
                </tr>
              </thead>
              <tbody>
                {topCandidates.map((c, index) => (
                  <tr key={index} className="text-center">
                    <td className="border p-2">{index + 1}</td>
                    <td className="border p-2">{c.Candidate}</td>
                    <td className="border p-2">{c.Aptitude}</td>
                    <td className="border p-2">{c.Coding}</td>
                    <td className="border p-2">{c.Communication}</td>
                    <td className="border p-2 font-bold">{c.Total_Score}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Checkbox to show all */}
            <div className="mt-4">
              <label className="flex items-center gap-2 cursor-pointer">
                <input
                  type="checkbox"
                  checked={showAll}
                  onChange={() => setShowAll(!showAll)}
                  className="cursor-pointer"
                />
                Show All Candidates Scores
              </label>
            </div>

            {showAll && (
              <div className="mt-4">
                <h2 className="text-lg font-semibold mb-2">All Candidates</h2>
                <div className="overflow-x-auto">
                  <table className="w-full border-collapse border border-gray-300">
                    <thead>
                      <tr className="bg-gray-200">
                        <th className="border p-2">#</th>
                        <th className="border p-2">Candidate</th>
                        <th className="border p-2">Aptitude</th>
                        <th className="border p-2">Coding</th>
                        <th className="border p-2">Communication</th>
                        <th className="border p-2">Total Score</th>
                      </tr>
                    </thead>
                    <tbody>
                      {candidates.map((c, index) => (
                        <tr key={index} className="text-center">
                          <td className="border p-2">{index + 1}</td>
                          <td className="border p-2">{c.Candidate}</td>
                          <td className="border p-2">{c.Aptitude}</td>
                          <td className="border p-2">{c.Coding}</td>
                          <td className="border p-2">{c.Communication}</td>
                          <td className="border p-2 font-bold">{c.Total_Score}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
