import React, { useState } from "react";

const CandidatesGenerator = () => {
  const [totalCandidates, setTotalCandidates] = useState(15);
  const [topCandidates, setTopCandidates] = useState(4);

  const increment = () => setTotalCandidates(totalCandidates + 1);
  const decrement = () =>
    setTotalCandidates(totalCandidates > 1 ? totalCandidates - 1 : 1);

  return (
    <div className="max-w-sm mx-auto bg-gray-100 p-6 rounded-2xl shadow-md">
      <h2 className="text-lg font-semibold mb-4">Candidate Generator Controls</h2>

      {/* Input with buttons */}
      <label className="block mb-2 font-medium text-gray-700">
        Enter total number of candidates shortlisted:
      </label>
      <div className="flex items-center border rounded-lg bg-white w-full">
        <button
          onClick={decrement}
          className="px-3 py-2 text-lg font-bold text-gray-600 hover:bg-gray-200 rounded-l-lg"
        >
          âˆ’
        </button>
        <input
          type="number"
          value={totalCandidates}
          onChange={(e) => setTotalCandidates(Number(e.target.value))}
          className="w-full text-center border-x border-gray-200 focus:outline-none"
        />
        <button
          onClick={increment}
          className="px-3 py-2 text-lg font-bold text-gray-600 hover:bg-gray-200 rounded-r-lg"
        >
          +
        </button>
      </div>

      {/* Slider */}
      <div className="mt-6">
        <label className="block mb-2 font-medium text-gray-700">
          Number of top candidates to select:
        </label>
        <input
          type="range"
          min="1"
          max={totalCandidates}
          value={topCandidates}
          onChange={(e) => setTopCandidates(Number(e.target.value))}
          className="w-full accent-red-500"
        />
        <p className="text-red-500 font-semibold mt-1">{topCandidates}</p>
      </div>
    </div>
  );
};

export default CandidatesGenerator;
