import React, { useState, useEffect } from 'react';
import { 
  Clock, 
  X, 
  ChevronRight, 
  ChevronLeft, 
  Flag, 
  Save, 
  CheckCircle,
  AlertTriangle,
  Play,
  Upload,
  Mic,
  Video,
  Code,
  FileText,
  User,
  Bell,
  Briefcase,
  Users,
  BarChart3,
  Settings,
  Eye,
  ArrowLeft
} from 'lucide-react';

const AssessmentRunner = () => {
  const [currentStep, setCurrentStep] = useState('instructions'); // instructions, test, completed
  const [isPreviewMode, setIsPreviewMode] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [timeRemaining, setTimeRemaining] = useState(45 * 60); // 45 minutes in seconds
  const [answers, setAnswers] = useState({});
  const [flaggedQuestions, setFlaggedQuestions] = useState(new Set());
  const [showExitModal, setShowExitModal] = useState(false);
  const [showSubmitModal, setShowSubmitModal] = useState(false);

  // Sample assessment data
  const assessment = {
    name: 'JavaScript Fundamentals',
    type: 'Technical',
    duration: '45 minutes',
    totalQuestions: 15,
    instructions: [
      'You have 45 minutes to complete this assessment',
      'Each question must be answered before moving to the next',
      'You can navigate between questions using the sidebar',
      'Use the "Mark for Review" feature to flag questions you want to revisit',
      'Do not switch tabs or leave this page during the assessment',
      'Your progress is automatically saved'
    ]
  };

  const questions = [
    {
      id: 1,
      type: 'mcq',
      question: 'What is the correct way to declare a variable in JavaScript?',
      options: [
        'var myVariable = "Hello";',
        'variable myVariable = "Hello";',
        'v myVariable = "Hello";',
        'declare myVariable = "Hello";'
      ]
    },
    {
      id: 2,
      type: 'mcq-multiple',
      question: 'Which of the following are valid JavaScript data types? (Select all that apply)',
      options: [
        'String',
        'Boolean',
        'Integer',
        'Object',
        'Undefined'
      ]
    },
    {
      id: 3,
      type: 'coding',
      question: 'Write a function that returns the sum of two numbers:',
      placeholder: 'function add(a, b) {\n  // Your code here\n}'
    },
    {
      id: 4,
      type: 'text',
      question: 'Explain the difference between "==" and "===" in JavaScript:',
      placeholder: 'Enter your explanation here...'
    },
    {
      id: 5,
      type: 'video',
      question: 'Record a 2-minute video explaining your approach to debugging JavaScript code:',
      maxDuration: '2 minutes'
    }
  ];

  // Timer effect
  useEffect(() => {
    if (currentStep === 'test' && timeRemaining > 0) {
      const timer = setInterval(() => {
        setTimeRemaining(prev => {
          if (prev <= 1) {
            handleSubmit();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [currentStep, timeRemaining]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const handleAnswer = (questionId, answer) => {
    setAnswers(prev => ({ ...prev, [questionId]: answer }));
  };

  const toggleFlag = (questionId) => {
    setFlaggedQuestions(prev => {
      const newSet = new Set(prev);
      if (newSet.has(questionId)) {
        newSet.delete(questionId);
      } else {
        newSet.add(questionId);
      }
      return newSet;
    });
  };

  const getQuestionStatus = (questionId) => {
    if (answers[questionId]) return 'answered';
    if (flaggedQuestions.has(questionId)) return 'flagged';
    return 'not-answered';
  };

  const handleSubmit = () => {
    setCurrentStep('completed');
  };

  const getUnansweredCount = () => {
    return questions.filter(q => !answers[q.id]).length;
  };

  // Instructions Page
  const InstructionsPage = () => (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg shadow-lg max-w-2xl w-full p-8">
        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <FileText className="w-8 h-8 text-blue-600" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Assessment Instructions</h1>
          <p className="text-gray-600">Please read the following instructions carefully before starting</p>
        </div>

        {/* Assessment Details */}
        <div className="bg-blue-50 rounded-lg p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">{assessment.name}</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
            <div>
              <span className="font-medium text-gray-700">Type:</span>
              <p className="text-gray-600">{assessment.type}</p>
            </div>
            <div>
              <span className="font-medium text-gray-700">Duration:</span>
              <p className="text-gray-600">{assessment.duration}</p>
            </div>
            <div>
              <span className="font-medium text-gray-700">Questions:</span>
              <p className="text-gray-600">{assessment.totalQuestions} questions</p>
            </div>
          </div>
        </div>

        {/* Instructions List */}
        <div className="mb-8">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Instructions:</h3>
          <ul className="space-y-3">
            {assessment.instructions.map((instruction, index) => (
              <li key={index} className="flex items-start space-x-3">
                <div className="w-6 h-6 bg-blue-600 rounded-full flex items-center justify-center flex-shrink-0 mt-0.5">
                  <span className="text-white text-sm font-medium">{index + 1}</span>
                </div>
                <p className="text-gray-700">{instruction}</p>
              </li>
            ))}
          </ul>
        </div>

        {/* Start Button */}
        <div className="text-center">
          <button
            onClick={() => setCurrentStep('test')}
            className="bg-blue-600 text-white px-8 py-3 rounded-lg font-medium hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 inline-flex items-center space-x-2"
          >
            <Play className="w-5 h-5" />
            <span>Start Assessment</span>
          </button>
        </div>
      </div>
    </div>
  );

  // Question Component
  const QuestionComponent = ({ question, answer, onAnswer }) => {
    switch (question.type) {
      case 'mcq':
        return (
          <div className="space-y-4">
            {question.options.map((option, index) => (
              <label key={index} className="d-flex align-items-center gap-2 p-2 border rounded cursor-pointer">
                <input
                  type="radio"
                  name={`question-${question.id}`}
                  value={option}
                  checked={answer === option}
                  onChange={(e) => onAnswer(question.id, e.target.value)}
                  className="form-check-input"
                />
                <span>{option}</span>
              </label>
            ))}
          </div>
        );

      case 'mcq-multiple':
        const selectedOptions = answer || [];
        return (
          <div className="space-y-4">
            {question.options.map((option, index) => (
              <label key={index} className="d-flex align-items-center gap-2 p-2 border rounded cursor-pointer">
                <input
                  type="checkbox"
                  value={option}
                  checked={selectedOptions.includes(option)}
                  onChange={(e) => {
                    const newSelected = e.target.checked
                      ? [...selectedOptions, option]
                      : selectedOptions.filter(opt => opt !== option);
                    onAnswer(question.id, newSelected);
                  }}
                  className="form-check-input"
                />
                <span>{option}</span>
              </label>
            ))}
          </div>
        );

      case 'coding':
        return (
          <div className="border border-gray-300 rounded-lg overflow-hidden">
            <div className="bg-gray-800 text-white p-3 flex items-center space-x-2">
              <Code className="w-4 h-4" />
              <span className="text-sm">JavaScript</span>
            </div>
            <textarea
              value={answer || ''}
              onChange={(e) => onAnswer(question.id, e.target.value)}
              placeholder={question.placeholder}
              className="w-full h-64 p-4 font-mono text-sm bg-gray-900 text-green-400 border-none resize-none focus:ring-0"
              style={{ fontFamily: 'Monaco, Consolas, monospace' }}
            />
          </div>
        );

      case 'text':
        return (
          <textarea
            value={answer || ''}
            onChange={(e) => onAnswer(question.id, e.target.value)}
            placeholder={question.placeholder}
            rows={6}
            className="w-full p-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
          />
        );

      case 'video':
        return (
          <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
            <Video className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h4 className="text-lg font-medium text-gray-900 mb-2">Record Video Response</h4>
            <p className="text-gray-600 mb-4">Maximum duration: {question.maxDuration}</p>
            <div className="space-x-4">
              <button className="inline-flex items-center px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700">
                <Mic className="w-4 h-4 mr-2" />
                Start Recording
              </button>
              <button className="inline-flex items-center px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50">
                <Upload className="w-4 h-4 mr-2" />
                Upload File
              </button>
            </div>
          </div>
        );

      default:
        return <div>Unsupported question type</div>;
    }
  };

  // Test Runner
  const TestRunner = () => (
    <div className="min-h-screen bg-gray-50">
      {/* Preview Mode Banner */}
      {isPreviewMode && (
        <div className="bg-orange-100 border-b border-orange-200 px-4 py-3">
          <div className="flex items-center justify-between max-w-7xl mx-auto">
            <div className="flex items-center space-x-2">
              <Eye className="w-5 h-5 text-orange-600" />
              <span className="text-orange-800 font-medium">Preview Mode</span>
              <span className="text-orange-600">- This is how candidates will see the assessment</span>
            </div>
            <button 
              onClick={() => setIsPreviewMode(false)}
              className="text-orange-600 hover:text-orange-800"
            >
              <X className="w-5 h-5" />
            </button>
          </div>
        </div>
      )}

      {/* Recruiter Sidebar (Preview Mode) */}
      {isPreviewMode && (
        <div className="fixed left-0 top-0 h-full w-64 bg-white shadow-lg z-10">
          <div className="p-6 border-b">
            <h2 className="text-xl font-bold text-gray-800">TalentFlow</h2>
          </div>
          <nav className="p-4">
            <div className="space-y-2">
              <a href="#" className="flex items-center space-x-3 p-3 rounded-lg text-gray-600 hover:bg-gray-100">
                <Briefcase className="w-5 h-5" />
                <span>Jobs</span>
              </a>
              <a href="#" className="flex items-center space-x-3 p-3 rounded-lg text-gray-600 hover:bg-gray-100">
                <Users className="w-5 h-5" />
                <span>Candidates</span>
              </a>
              <a href="#" className="flex items-center space-x-3 p-3 rounded-lg text-gray-600 hover:bg-gray-100">
                <BarChart3 className="w-5 h-5" />
                <span>Pipeline</span>
              </a>
              <a href="#" className="flex items-center space-x-3 p-3 rounded-lg text-gray-600 hover:bg-gray-100">
                <BarChart3 className="w-5 h-5" />
                <span>Analytics</span>
              </a>
              <a href="#" className="flex items-center space-x-3 p-3 rounded-lg bg-blue-50 text-blue-600">
                <FileText className="w-5 h-5" />
                <span>Assessments</span>
              </a>
              <a href="#" className="flex items-center space-x-3 p-3 rounded-lg text-gray-600 hover:bg-gray-100">
                <Settings className="w-5 h-5" />
                <span>Settings</span>
              </a>
            </div>
          </nav>
        </div>
      )}

      <div className={isPreviewMode ? 'ml-64' : ''}>
        {/* Recruiter Topbar (Preview Mode) */}
        {isPreviewMode && (
          <div className="bg-white shadow-sm border-b px-6 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <button className="flex items-center space-x-2 text-gray-600 hover:text-gray-800">
                  <ArrowLeft className="w-4 h-4" />
                  <span>Back to Assessments</span>
                </button>
              </div>
              <div className="flex items-center space-x-4">
                <Bell className="w-5 h-5 text-gray-500 cursor-pointer" />
                <div className="flex items-center space-x-2 cursor-pointer">
                  <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center">
                    <User className="w-4 h-4 text-white" />
                  </div>
                  <span className="text-sm text-gray-700">John Recruiter</span>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Test Header */}
        <div className="bg-white shadow-sm border-b px-6 py-4">
          <div className="flex items-center justify-between">
            <h1 className="text-xl font-semibold text-gray-900">{assessment.name}</h1>
            <div className="flex items-center space-x-6">
              <div className="flex items-center space-x-2 text-red-600">
                <Clock className="w-5 h-5" />
                <span className="font-mono text-lg">{formatTime(timeRemaining)}</span>
              </div>
              <button
                onClick={() => setShowExitModal(true)}
                className="text-gray-600 hover:text-red-600 flex items-center space-x-2"
              >
                <X className="w-5 h-5" />
                <span>Exit</span>
              </button>
            </div>
          </div>
        </div>

        <div className="flex">
          {/* Question Navigation Sidebar */}
          <div className="w-64 bg-white shadow-sm h-screen overflow-y-auto">
            <div className="p-4 border-b">
              <h3 className="font-medium text-gray-900">Questions</h3>
              <p className="text-sm text-gray-600">{Object.keys(answers).length} of {questions.length} answered</p>
            </div>
            <div className="p-2">
              {questions.map((question, index) => {
                const status = getQuestionStatus(question.id);
                return (
                  <button
                    key={question.id}
                    onClick={() => setCurrentQuestion(index)}
                    className={`w-full p-3 mb-2 rounded-lg text-left flex items-center justify-between ${
                      currentQuestion === index
                        ? 'bg-blue-100 text-blue-700 border-2 border-blue-300'
                        : status === 'answered'
                        ? 'bg-green-50 text-green-700 border border-green-200'
                        : status === 'flagged'
                        ? 'bg-yellow-50 text-yellow-700 border border-yellow-200'
                        : 'bg-gray-50 text-gray-600 border border-gray-200'
                    }`}
                  >
                    <span className="font-medium">Question {index + 1}</span>
                    {status === 'answered' && <CheckCircle className="w-4 h-4" />}
                    {status === 'flagged' && <Flag className="w-4 h-4" />}
                  </button>
                );
              })}
            </div>
          </div>

          {/* Main Question Area */}
          <div className="flex-1 p-8">
            <div className="max-w-4xl">
              {/* Question Counter */}
              <div className="mb-6">
                <span className="text-sm text-gray-500">
                  Question {currentQuestion + 1} of {questions.length}
                </span>
              </div>

              {/* Question */}
              <div className="mb-8">
                <h2 className="text-xl font-medium text-gray-900 mb-6">
                  {questions[currentQuestion]?.question}
                </h2>
                <QuestionComponent
                  question={questions[currentQuestion]}
                  answer={answers[questions[currentQuestion]?.id]}
                  onAnswer={handleAnswer}
                />
              </div>

              {/* Action Buttons */}
              <div className="flex items-center justify-between">
                <div className="flex space-x-4">
                  <button
                    onClick={() => setCurrentQuestion(Math.max(0, currentQuestion - 1))}
                    disabled={currentQuestion === 0}
                    className="flex items-center space-x-2 px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <ChevronLeft className="w-4 h-4" />
                    <span>Previous</span>
                  </button>
                  <button
                    onClick={() => toggleFlag(questions[currentQuestion]?.id)}
                    className={`flex items-center space-x-2 px-4 py-2 border rounded-lg ${
                      flaggedQuestions.has(questions[currentQuestion]?.id)
                        ? 'border-yellow-300 bg-yellow-50 text-yellow-700'
                        : 'border-gray-300 text-gray-700 hover:bg-gray-50'
                    }`}
                  >
                    <Flag className="w-4 h-4" />
                    <span>Mark for Review</span>
                  </button>
                </div>

                <div className="flex space-x-4">
                  {currentQuestion < questions.length - 1 ? (
                    <button
                      onClick={() => setCurrentQuestion(currentQuestion + 1)}
                      className="flex items-center space-x-2 px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                    >
                      <Save className="w-4 h-4" />
                      <span>Save & Next</span>
                      <ChevronRight className="w-4 h-4" />
                    </button>
                  ) : (
                    <button
                      onClick={() => setShowSubmitModal(true)}
                      className="flex items-center space-x-2 px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                    >
                      <CheckCircle className="w-4 h-4" />
                      <span>Submit Assessment</span>
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Exit Confirmation Modal */}
      {showExitModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
            <div className="flex items-center space-x-3 mb-4">
              <AlertTriangle className="w-6 h-6 text-red-600" />
              <h3 className="text-lg font-semibold text-gray-900">Exit Assessment?</h3>
            </div>
            <p className="text-gray-600 mb-6">
              Are you sure you want to exit? Your progress will be saved, but you may not be able to resume.
            </p>
            <div className="flex space-x-4">
              <button
                onClick={() => setShowExitModal(false)}
                className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
              >
                Continue Test
              </button>
              <button
                onClick={() => setCurrentStep('completed')}
                className="flex-1 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
              >
                Exit Anyway
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Submit Confirmation Modal */}
      {showSubmitModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
            <div className="flex items-center space-x-3 mb-4">
              <CheckCircle className="w-6 h-6 text-blue-600" />
              <h3 className="text-lg font-semibold text-gray-900">Submit Assessment?</h3>
            </div>
            {getUnansweredCount() > 0 ? (
              <p className="text-gray-600 mb-6">
                You have {getUnansweredCount()} unanswered question(s). Are you sure you want to submit?
              </p>
            ) : (
              <p className="text-gray-600 mb-6">
                All questions have been answered. Ready to submit your assessment?
              </p>
            )}
            <div className="flex space-x-4">
              <button
                onClick={() => setShowSubmitModal(false)}
                className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
              >
                Go Back
              </button>
              <button
                onClick={handleSubmit}
                className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
              >
                Submit Anyway
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );

  // Completion Page
  const CompletionPage = () => (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg shadow-lg max-w-2xl w-full p-8 text-center">
        <div className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6">
          <CheckCircle className="w-10 h-10 text-green-600" />
        </div>
        
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Assessment Submitted Successfully!</h1>
        <p className="text-lg text-gray-600 mb-8">
          Thank you for completing the {assessment.name} assessment.
        </p>
        
        <div className="bg-blue-50 rounded-lg p-6 mb-8">
          <h3 className="font-semibold text-gray-900 mb-2">What happens next?</h3>
          <p className="text-gray-700">
            The recruiter will review your results shortly and get back to you with the next steps in the hiring process.
          </p>
        </div>

        <div className="space-y-4">
          <button className="w-full bg-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2">
            Return to Dashboard
          </button>
          {isPreviewMode && (
            <button
              onClick={() => {
                setCurrentStep('instructions');
                setCurrentQuestion(0);
                setTimeRemaining(45 * 60);
                setAnswers({});
                setFlaggedQuestions(new Set());
              }}
              className="w-full border border-gray-300 text-gray-700 py-3 px-6 rounded-lg font-medium hover:bg-gray-50"
            >
              Preview Again
            </button>
          )}
        </div>
      </div>
    </div>
  );

  // Mode Toggle (for demo purposes)
  const ModeToggle = () => (
    <div className="fixed top-4 right-4 z-50">
      <button
        onClick={() => setIsPreviewMode(!isPreviewMode)}
        className={`px-4 py-2 rounded-lg text-sm font-medium ${
          isPreviewMode 
            ? 'bg-orange-600 text-white' 
            : 'bg-gray-600 text-white'
        }`}
      >
        {isPreviewMode ? 'Recruiter Preview' : 'Candidate View'}
      </button>
    </div>
  );

  // Render based on current step
  return (
    <div>
      <ModeToggle />
      {currentStep === 'instructions' && <InstructionsPage />}
      {currentStep === 'test' && <TestRunner />}
      {currentStep === 'completed' && <CompletionPage />}
    </div>
  );
};

export default AssessmentRunner;
