/* eslint-disable react/prop-types */
import { useState, useEffect, useRef } from "react";
import { Icon } from "@iconify/react";
import { Link, NavLink, useLocation } from "react-router-dom";
import { menuConfig } from "../../config/menuConfig"; // Import the menu configuration

// A reusable component for rendering dropdown menu items
const DropdownMenuItem = ({ item, isOpen, onToggle }) => {
  const submenuRef = useRef(null);
  return (
    <li className={`dropdown ${isOpen ? "open" : ""}`}>
      <Link
        to="#"
        onClick={(e) => {
          e.preventDefault();
          onToggle();
        }}
      >
        {item.icon && <Icon icon={item.icon} className="menu-icon" />}
        <span>{item.title}</span>
      </Link>
      <ul
        ref={submenuRef}
        className="sidebar-submenu"
        style={{
          // Animate the dropdown opening/closing
          maxHeight: isOpen ? `${submenuRef.current?.scrollHeight}px` : "0px",
        }}
      >
        {item.children.map((child) => (
          <li key={child.to}>
            <NavLink
              to={child.to}
              className={({ isActive }) => (isActive ? "active-page" : "")}
            >
              <i
                className={`ri-circle-fill circle-icon ${child.colorClass} w-auto`}
              />{" "}
              {child.title}
            </NavLink>
          </li>
        ))}
      </ul>
    </li>
  );
};

const Sidebars = ({ children }) => {
  const [sidebarActive, setSidebarActive] = useState(false);
  const [mobileMenu, setMobileMenu] = useState(false);
  const [openDropdownId, setOpenDropdownId] = useState(null);
  const location = useLocation();

  // This effect ensures the correct dropdown is open when the page loads
  // or when the user navigates to a new page.
  useEffect(() => {
    const activeDropdown = menuConfig.find(
      (item) =>
        item.isDropdown &&
        item.children.some((child) => child.to === location.pathname)
    );

    if (activeDropdown) {
      setOpenDropdownId(activeDropdown.id);
    }
    // When navigating to a non-dropdown page, you might want to close all dropdowns.
    // If not, you can remove the `else` block.
    else {
      // Optional: Close dropdowns if no child is active.
      // setOpenDropdownId(null);
    }
  }, [location.pathname]);

  const toggleDropdown = (id) => {
    // If the clicked dropdown is already open, close it. Otherwise, open it.
    setOpenDropdownId(openDropdownId === id ? null : id);
  };

  const toggleSidebar = () => {
    setSidebarActive(!sidebarActive);
  };

  const toggleMobileMenu = () => {
    setMobileMenu(!mobileMenu);
  };

    return (
        <section className={mobileMenu ? "overlay active" : "overlay "}>
            {/* sidebar */}
            <aside
                className={
                    sidebarActive
                        ? "sidebar active "
                        : mobileMenu
                            ? "sidebar sidebar-open"
                            : "sidebar"
                }
            >
                <button
                    onClick={toggleMobileMenu}
                    type='button'
                    className='sidebar-close-btn'
                >
                    <Icon icon='radix-icons:cross-2' />
                </button>
                <div>
                    <Link to='/' className='sidebar-logo'>
                        <img
                            src='assets/images/logo.png'
                            alt='site logo'
                            className='light-logo'
                        />
                        <img
                            src='assets/images/logo-light.png'
                            alt='site logo'
                            className='dark-logo'
                        />
                        <img
                            src='assets/images/logo-icon.png'
                            alt='site logo'
                            className='logo-icon'
                        />
                    </Link>
                </div>
                <div className='sidebar-menu-area'>
                    <ul className='sidebar-menu' id='sidebar-menu'>
                        {menuConfig.map((item) => {
                            if (item.isGroupTitle) {
                                return (
                                    <li key={item.title} className="sidebar-menu-group-title">
                                        {item.title}
                                    </li>
                                );
                            }
                            if (item.isDropdown) {
                                return (
                                    <DropdownMenuItem
                                        key={item.id}
                                        item={item}
                                        isOpen={openDropdownId === item.id}
                                        onToggle={() => toggleDropdown(item.id)}
                                    />
                                );
                            }
                            // This renders a standard, non-dropdown menu item
                            return (
                                <li key={item.id}>
                                    <NavLink to={item.to} className={({ isActive }) => (isActive ? "active-page" : "")}>
                                        {item.icon && (
                                            <Icon icon={item.icon} className="menu-icon" />
                                        )}
                                        <span>{item.title}</span>
                                    </NavLink>
                                </li>
                            );
                        })}
                    </ul>
                </div>
            </aside>

            <main
                className={sidebarActive ? "dashboard-main active" : "dashboard-main"}
            >
                <div className='navbar-header'>
                    <div className='row align-items-center justify-content-between'>
                        <div className='col-auto'>
                            <div className='d-flex flex-wrap align-items-center gap-4'>
                                <button
                                    type='button'
                                    className='sidebar-toggle'
                                    onClick={toggleSidebar}
                                >
                                    {sidebarActive ? (
                                        <Icon
                                            icon='iconoir:arrow-right'
                                            className='icon text-2xl non-active'
                                        />
                                    ) : (
                                        <Icon
                                            icon='heroicons:bars-3-solid'
                                            className='icon text-2xl non-active '
                                        />
                                    )}
                                </button>
                                <button
                                    onClick={toggleMobileMenu}
                                    type='button'
                                    className='sidebar-mobile-toggle'
                                >
                                    <Icon icon='heroicons:bars-3-solid' className='icon' />
                                </button>
                                <form className='navbar-search'>
                                    <input type='text' name='search' placeholder='Search' />
                                    <Icon icon='ion:search-outline' className='icon' />
                                </form>
                            </div>
                        </div>
                        <div className='col-auto'>
                            <div className='d-flex flex-wrap align-items-center gap-3'>
                                {/* Header action buttons (e.g., Theme Toggle, Notifications) go here */}
                            </div>
                        </div>
                    </div>
                </div>

                <div className="dashboard-main-body">{children}</div>

                {/* Footer section */}
                <footer className='d-footer'>
                    <div className='row align-items-center justify-content-between'>
                        <div className='col-auto'>
                            <p className='mb-0'>Â© 2025 WowDash. All Rights Reserved.</p>
                        </div>
                        <div className='col-auto'>
                            <p className='mb-0'>
                                Made by <span className='text-primary-600'>wowtheme7</span>
                            </p>
                        </div>
                    </div>
                </footer>
            </main>
        </section>
    );
};

export default Sidebars;
