import React from 'react'
import Sidebars from '../layout/dashboard/Sidebars'
import { Icon } from "@iconify/react";
import { Link } from "react-router-dom";


const CandidatesGenerator = () => {
    const [value, setValue] = useState(4);

    const handleChange = (e) => {
        setValue(Number(e.target.value));
    };


    return (
        <>
            <Sidebars>
                <div className='d-flex flex-wrap align-items-center justify-content-between gap-3 mb-24'>
                    <h6 className='fw-semibold mb-0'>Candidate Generator Controls</h6>
                </div>
                <div style={{ display: "flex", justifyContent: "space-between", flexDirection: "row", }}>
                    <div style={{ backgroundColor: "rebeccapurple" }}>
                        <div className="w-80 bg-white shadow-sm border-r border-gray-200 p-6">
                            <div className="mb-8">
                                <div className="mb-6">
                                    <label className="block text-sm text-gray-600 mb-3">
                                        Enter total number of candidates shortlisted:
                                    </label>
                                    <div className="flex items-center border border-gray-300 rounded-lg overflow-hidden w-48">
                                        {/* Input field */}
                                        <input
                                            type="number"
                                            defaultValue="15"

                                            className="flex-1 px-3 py-2 text-lg font-medium text-gray-800 focus:outline-none"
                                        />


                                    </div>
                                </div>

                                <div
                                    className="absolute -top-6 left-0 transform -translate-x-1/2 text-red-500 font-semibold"
                                    style={{ left: `${(value / 20) * 100}%` }}
                                >
                                    {value}
                                </div>

                                {/* Range input with custom styles */}
                                <input
                                    type="range"
                                    min="1"
                                    max="20"
                                    value={value}
                                    onChange={handleChange}
                                    className="w-full h-2 rounded-lg appearance-none cursor-pointer"
                                    style={{
                                        background: `linear-gradient(to right, #ef4444 ${(value / 20) * 100}%, #e5e7eb ${(value / 20) * 100}%)`,
                                    }}
                                />

                                {/* Min/Max labels */}
                                <div className="flex justify-between text-xs text-gray-500 mt-1">
                                    <span>1</span>
                                    <span>20</span>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div style={{ height: "40vh", width: "80%", backgroundColor: "gray" }}>
                    </div>

                </div>





            </Sidebars>
        </>
    )
}

export default CandidatesGenerator;
