import React, { useState } from "react";

const CandidateGenerator = () => {
  const [totalCandidates, setTotalCandidates] = useState(15);
  const [topCandidates, setTopCandidates] = useState(4);

  const increment = () => setTotalCandidates(totalCandidates + 1);
  const decrement = () =>
    setTotalCandidates(totalCandidates > 1 ? totalCandidates - 1 : 1);

  return (
    <div className="max-w-sm mx-auto bg-gray-100 p-6 rounded-2xl shadow-md">
      {/* Label */}
      <label className="form-label block mb-2 font-medium text-gray-700">
        Enter total number of candidates shortlisted:
      </label>

      {/* Number input with buttons */}
      <div className="d-flex align-items-center border rounded-lg bg-white w-full">
        <input
          type="number"
          value={totalCandidates}
          onChange={(e) => setTotalCandidates(Number(e.target.value))}
          className="form-control text-center border-0 focus:ring-0 focus:outline-none"
        />
        <button
          onClick={decrement}
          className="btn btn-light border-start rounded-0 hover:bg-gray-200"
        >
          âˆ’
        </button>
        <button
          onClick={increment}
          className="btn btn-light border-start rounded-0 hover:bg-gray-200"
        >
          +
        </button>
      </div>

      {/* Slider */}
      <div className="mt-4">
        <label className="form-label block mb-2 font-medium text-gray-700">
          Number of top candidates to select:
        </label>
        <input
          type="range"
          min="1"
          max={totalCandidates}
          value={topCandidates}
          onChange={(e) => setTopCandidates(Number(e.target.value))}
          className="form-range w-full accent-red-500"
        />
        <p className="text-red-500 font-semibold mt-1">{topCandidates}</p>
      </div>
    </div>
  );
};

export default CandidateGenerator;
