import React, { useState } from 'react';
import { Icon } from '@iconify/react';

const PostJob = () => {
  const [formData, setFormData] = useState({
    jobTitle: '',
    company: '',
    location: '',
    jobType: '',
    workMode: '',
    experience: '',
    salary: '',
    currency: 'USD',
    jobDescription: '',
    requirements: '',
    skills: '',
    benefits: '',
    applicationDeadline: '',
    contactEmail: '',
    contactPhone: '',
    companyWebsite: '',
    department: '',
    educationLevel: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Job posting data:', formData);
    // Handle form submission here
    alert('Job posted successfully!');
  };

  const handleReset = () => {
    setFormData({
      jobTitle: '',
      company: '',
      location: '',
      jobType: '',
      workMode: '',
      experience: '',
      salary: '',
      currency: 'USD',
      jobDescription: '',
      requirements: '',
      skills: '',
      benefits: '',
      applicationDeadline: '',
      contactEmail: '',
      contactPhone: '',
      companyWebsite: '',
      department: '',
      educationLevel: ''
    });
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-12">
          {/* Header Section */}
          <div className="d-flex align-items-center gap-3 mb-4">
            <div className="d-flex align-items-center justify-content-center" 
                 style={{ 
                   width: '48px', 
                   height: '48px', 
                   background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                   borderRadius: '12px'
                 }}>
              <Icon icon="heroicons:plus" className="text-white" style={{ fontSize: '24px' }} />
            </div>
            <div>
              <h2 className="mb-0 fw-bold text-dark">Post a New Job</h2>
              <p className="mb-0 text-muted">Create and publish your job posting</p>
            </div>
          </div>

          {/* Main Form Card */}
          <div className="card border-0 shadow-sm">
            <div className="card-body p-4">
        <form onSubmit={handleSubmit}>
          <div className="row">
            {/* Basic Job Information */}
            <div className="col-12">
              <div className="bg-light rounded-3 p-3 mb-4">
                <div className="d-flex align-items-center gap-2 mb-3">
                  <Icon icon="heroicons:information-circle" className="text-primary" style={{ fontSize: '20px' }} />
                  <h5 className="mb-0 fw-semibold text-dark">Basic Information</h5>
                </div>
              </div>
            </div>

            <div className="col-12 mb-4">
              <div className="row g-3 p-3 bg-white border rounded-3" style={{ boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
                <div className="col-md-6">
                  <label htmlFor="jobTitle" className="form-label fw-medium text-dark mb-2">
                    Job Title <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control border-2 border-primary border-opacity-25"
                    style={{ 
                      borderRadius: '8px', 
                      padding: '12px 16px',
                      transition: 'all 0.3s ease',
                      backgroundColor: '#f8f9ff'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#0d6efd';
                      e.target.style.boxShadow = '0 0 0 0.25rem rgba(13, 110, 253, 0.15)';
                      e.target.style.backgroundColor = '#ffffff';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'rgba(13, 110, 253, 0.25)';
                      e.target.style.boxShadow = 'none';
                      e.target.style.backgroundColor = '#f8f9ff';
                    }}
                    id="jobTitle"
                    name="jobTitle"
                    value={formData.jobTitle}
                    onChange={handleInputChange}
                    placeholder="e.g. Senior Software Engineer"
                    required
                  />
                </div>
                <div className="col-md-6">
                  <label htmlFor="company" className="form-label fw-medium text-dark mb-2">
                    Company Name <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control border-2 border-primary border-opacity-25"
                    style={{ 
                      borderRadius: '8px', 
                      padding: '12px 16px',
                      transition: 'all 0.3s ease',
                      backgroundColor: '#f8f9ff'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#0d6efd';
                      e.target.style.boxShadow = '0 0 0 0.25rem rgba(13, 110, 253, 0.15)';
                      e.target.style.backgroundColor = '#ffffff';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'rgba(13, 110, 253, 0.25)';
                      e.target.style.boxShadow = 'none';
                      e.target.style.backgroundColor = '#f8f9ff';
                    }}
                    id="company"
                    name="company"
                    value={formData.company}
                    onChange={handleInputChange}
                    placeholder="e.g. Tech Solutions Inc."
                    required
                  />
                </div>
              </div>
            </div>

            <div className="col-12 mb-4">
              <div className="row g-3 p-3 bg-white border rounded-3" style={{ boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
                <div className="col-md-6">
                  <label htmlFor="location" className="form-label fw-medium text-dark mb-2">
                    Location <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control border-2 border-success border-opacity-25"
                    style={{ 
                      borderRadius: '8px', 
                      padding: '12px 16px',
                      transition: 'all 0.3s ease',
                      backgroundColor: '#f8fff8'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#198754';
                      e.target.style.boxShadow = '0 0 0 0.25rem rgba(25, 135, 84, 0.15)';
                      e.target.style.backgroundColor = '#ffffff';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'rgba(25, 135, 84, 0.25)';
                      e.target.style.boxShadow = 'none';
                      e.target.style.backgroundColor = '#f8fff8';
                    }}
                    id="location"
                    name="location"
                    value={formData.location}
                    onChange={handleInputChange}
                    placeholder="e.g. New York, NY"
                    required
                  />
                </div>
                <div className="col-md-6">
                  <label htmlFor="department" className="form-label fw-medium text-dark mb-2">
                    Department
                  </label>
                  <input
                    type="text"
                    className="form-control border-2 border-success border-opacity-25"
                    style={{ 
                      borderRadius: '8px', 
                      padding: '12px 16px',
                      transition: 'all 0.3s ease',
                      backgroundColor: '#f8fff8'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#198754';
                      e.target.style.boxShadow = '0 0 0 0.25rem rgba(25, 135, 84, 0.15)';
                      e.target.style.backgroundColor = '#ffffff';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'rgba(25, 135, 84, 0.25)';
                      e.target.style.boxShadow = 'none';
                      e.target.style.backgroundColor = '#f8fff8';
                    }}
                    id="department"
                    name="department"
                    value={formData.department}
                    onChange={handleInputChange}
                    placeholder="e.g. Engineering, Marketing"
                  />
                </div>
              </div>
            </div>

            {/* Job Type and Work Mode */}
            <div className="col-12 mb-4">
              <div className="row g-3 p-3 bg-white border rounded-3" style={{ boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
                <div className="col-md-4">
                  <label htmlFor="jobType" className="form-label fw-medium text-dark mb-2">
                    Job Type <span className="text-danger">*</span>
                  </label>
                  <select
                    className="form-select border-2 border-info border-opacity-25"
                    style={{ 
                      borderRadius: '8px', 
                      padding: '12px 16px',
                      transition: 'all 0.3s ease',
                      backgroundColor: '#f8fdff'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#0dcaf0';
                      e.target.style.boxShadow = '0 0 0 0.25rem rgba(13, 202, 240, 0.15)';
                      e.target.style.backgroundColor = '#ffffff';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'rgba(13, 202, 240, 0.25)';
                      e.target.style.boxShadow = 'none';
                      e.target.style.backgroundColor = '#f8fdff';
                    }}
                    id="jobType"
                    name="jobType"
                    value={formData.jobType}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select Job Type</option>
                    <option value="full-time">Full Time</option>
                    <option value="part-time">Part Time</option>
                    <option value="contract">Contract</option>
                    <option value="freelance">Freelance</option>
                    <option value="internship">Internship</option>
                  </select>
                </div>
                <div className="col-md-4">
                  <label htmlFor="workMode" className="form-label fw-medium text-dark mb-2">
                    Work Mode <span className="text-danger">*</span>
                  </label>
                  <select
                    className="form-select border-2 border-info border-opacity-25"
                    style={{ 
                      borderRadius: '8px', 
                      padding: '12px 16px',
                      transition: 'all 0.3s ease',
                      backgroundColor: '#f8fdff'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#0dcaf0';
                      e.target.style.boxShadow = '0 0 0 0.25rem rgba(13, 202, 240, 0.15)';
                      e.target.style.backgroundColor = '#ffffff';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'rgba(13, 202, 240, 0.25)';
                      e.target.style.boxShadow = 'none';
                      e.target.style.backgroundColor = '#f8fdff';
                    }}
                    id="workMode"
                    name="workMode"
                    value={formData.workMode}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select Work Mode</option>
                    <option value="remote">Remote</option>
                    <option value="on-site">On-site</option>
                    <option value="hybrid">Hybrid</option>
                  </select>
                </div>
                <div className="col-md-4">
                  <label htmlFor="experience" className="form-label fw-medium text-dark mb-2">
                    Experience Level <span className="text-danger">*</span>
                  </label>
                  <select
                    className="form-select border-2 border-info border-opacity-25"
                    style={{ 
                      borderRadius: '8px', 
                      padding: '12px 16px',
                      transition: 'all 0.3s ease',
                      backgroundColor: '#f8fdff'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#0dcaf0';
                      e.target.style.boxShadow = '0 0 0 0.25rem rgba(13, 202, 240, 0.15)';
                      e.target.style.backgroundColor = '#ffffff';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'rgba(13, 202, 240, 0.25)';
                      e.target.style.boxShadow = 'none';
                      e.target.style.backgroundColor = '#f8fdff';
                    }}
                    id="experience"
                    name="experience"
                    value={formData.experience}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select Experience</option>
                    <option value="entry-level">Entry Level (0-2 years)</option>
                    <option value="mid-level">Mid Level (2-5 years)</option>
                    <option value="senior-level">Senior Level (5-10 years)</option>
                    <option value="executive">Executive (10+ years)</option>
                  </select>
                </div>
              </div>
            </div>

            {/* Salary Information */}
            <div className="col-12 mt-4">
              <div className="d-flex align-items-center gap-2 mb-3">
                <Icon icon="heroicons:currency-dollar" className="text-success" style={{ fontSize: '20px' }} />
                <h5 className="mb-0 fw-semibold text-dark">Compensation</h5>
              </div>
            </div>

            <div className="col-md-8 mb-3">
              <label htmlFor="salary" className="form-label fw-medium text-dark mb-2">
                Salary Range
              </label>
              <input
                type="text"
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="salary"
                name="salary"
                value={formData.salary}
                onChange={handleInputChange}
                placeholder="e.g. $80,000 - $120,000 per year"
              />
            </div>

            <div className="col-md-4 mb-3">
              <label htmlFor="currency" className="form-label fw-medium text-dark mb-2">
                Currency
              </label>
              <select
                className="form-select border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="currency"
                name="currency"
                value={formData.currency}
                onChange={handleInputChange}
              >
                <option value="USD">USD ($)</option>
                <option value="EUR">EUR ()</option>
                <option value="GBP">GBP ()</option>
                <option value="INR">INR ()</option>
                <option value="CAD">CAD (C$)</option>
              </select>
            </div>

            {/* Job Description */}
            <div className="col-12 mt-4">
              <div className="d-flex align-items-center gap-2 mb-3">
                <Icon icon="heroicons:document-text" className="text-info" style={{ fontSize: '20px' }} />
                <h5 className="mb-0 fw-semibold text-dark">Job Details</h5>
              </div>
            </div>

            <div className="col-12 mb-3">
              <label htmlFor="jobDescription" className="form-label fw-medium text-dark mb-2">
                Job Description <span className="text-danger">*</span>
              </label>
              <textarea
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="jobDescription"
                name="jobDescription"
                rows="5"
                value={formData.jobDescription}
                onChange={handleInputChange}
                placeholder="Describe the role, responsibilities, and what the candidate will be doing..."
                required
              ></textarea>
            </div>

            <div className="col-12 mb-3">
              <label htmlFor="requirements" className="form-label fw-medium text-dark mb-2">
                Requirements & Qualifications <span className="text-danger">*</span>
              </label>
              <textarea
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="requirements"
                name="requirements"
                rows="4"
                value={formData.requirements}
                onChange={handleInputChange}
                placeholder="List the required qualifications, experience, and skills..."
                required
              ></textarea>
            </div>

            <div className="col-md-6 mb-3">
              <label htmlFor="skills" className="form-label fw-medium text-dark mb-2">
                Required Skills
              </label>
              <textarea
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="skills"
                name="skills"
                rows="3"
                value={formData.skills}
                onChange={handleInputChange}
                placeholder="e.g. JavaScript, React, Node.js, SQL..."
              ></textarea>
            </div>

            <div className="col-md-6 mb-3">
              <label htmlFor="educationLevel" className="form-label fw-medium text-dark mb-2">
                Education Level
              </label>
              <select
                className="form-select border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="educationLevel"
                name="educationLevel"
                value={formData.educationLevel}
                onChange={handleInputChange}
              >
                <option value="">Select Education Level</option>
                <option value="high-school">High School</option>
                <option value="associate">Associate Degree</option>
                <option value="bachelor">Bachelor's Degree</option>
                <option value="master">Master's Degree</option>
                <option value="phd">PhD</option>
                <option value="not-required">Not Required</option>
              </select>
            </div>

            <div className="col-12 mb-3">
              <label htmlFor="benefits" className="form-label fw-medium text-dark mb-2">
                Benefits & Perks
              </label>
              <textarea
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="benefits"
                name="benefits"
                rows="3"
                value={formData.benefits}
                onChange={handleInputChange}
                placeholder="e.g. Health insurance, 401k, flexible hours, remote work..."
              ></textarea>
            </div>

            {/* Application Details */}
            <div className="col-12 mt-4">
              <div className="d-flex align-items-center gap-2 mb-3">
                <Icon icon="heroicons:calendar-days" className="text-warning" style={{ fontSize: '20px' }} />
                <h5 className="mb-0 fw-semibold text-dark">Application Details</h5>
              </div>
            </div>

            <div className="col-md-4 mb-3">
              <label htmlFor="applicationDeadline" className="form-label fw-medium text-dark mb-2">
                Application Deadline
              </label>
              <input
                type="date"
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="applicationDeadline"
                name="applicationDeadline"
                value={formData.applicationDeadline}
                onChange={handleInputChange}
              />
            </div>

            <div className="col-md-4 mb-3">
              <label htmlFor="contactEmail" className="form-label fw-medium text-dark mb-2">
                Contact Email <span className="text-danger">*</span>
              </label>
              <input
                type="email"
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="contactEmail"
                name="contactEmail"
                value={formData.contactEmail}
                onChange={handleInputChange}
                placeholder="hr@company.com"
                required
              />
            </div>

            <div className="col-md-4 mb-3">
              <label htmlFor="contactPhone" className="form-label fw-medium text-dark mb-2">
                Contact Phone
              </label>
              <input
                type="tel"
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="contactPhone"
                name="contactPhone"
                value={formData.contactPhone}
                onChange={handleInputChange}
                placeholder="+1 (555) 123-4567"
              />
            </div>

            <div className="col-12 mb-4">
              <label htmlFor="companyWebsite" className="form-label fw-medium text-dark mb-2">
                Company Website
              </label>
              <input
                type="url"
                className="form-control border-0 bg-light"
                style={{ borderRadius: '8px', padding: '12px 16px' }}
                id="companyWebsite"
                name="companyWebsite"
                value={formData.companyWebsite}
                onChange={handleInputChange}
                placeholder="https://www.company.com"
              />
            </div>

            {/* Submit Buttons */}
            <div className="col-12 mt-4">
              <div className="d-flex align-items-center gap-2 mb-3">
                <Icon icon="heroicons:rocket-launch" className="text-purple" style={{ fontSize: '20px', color: '#8b5cf6' }} />
                <h5 className="mb-0 fw-semibold text-dark">Publish Job</h5>
              </div>
              <div className="d-flex justify-content-between align-items-center">
                <div className="d-flex gap-3">
                  <button
                    type="button"
                    className="btn btn-outline-secondary px-4"
                    onClick={handleReset}
                  >
                    <Icon icon="heroicons:arrow-path" className="me-2" />
                    Reset Form
                  </button>
                  <button
                    type="button"
                    className="btn btn-outline-primary px-4"
                  >
                    <Icon icon="heroicons:eye" className="me-2" />
                    Preview Job
                  </button>
                </div>
                <button
                  type="submit"
                  className="btn btn-primary px-5 py-2"
                  style={{ 
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    border: 'none',
                    borderRadius: '8px'
                  }}
                >
                  <Icon icon="heroicons:paper-airplane" className="me-2" />
                  Post Job Now
                </button>
              </div>
            </div>
          </div>
        </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostJob;
